import { Fragment, useState} from "react";
import Login from "./components/Layout/Login";
import Header from "./components/Layout/Header";
import PhotoGallery from "./components/FrontPage/PhotoGallery"
import FrontBanner from "./components/FrontPage/FrontBanner"
import React from "react";
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginActive, setLoginActive] = useState(false);

  const loginHandler = (email, password) => {
    // We should of course check email and password
    // But it's just a dummy/ demo anyways
    setIsLoggedIn(true);
  };

  const LoginActiveHandler = (event) => {

    setLoginActive(true);

  };
  const LoginNotActiveHandler = event =>{
    setLoginActive(false)
  }
  return (
    <Fragment>
     {loginActive && <Login on onLoginClick={LoginNotActiveHandler}/>}
      <Header onLoginClick={LoginActiveHandler} />
      <FrontBanner />
      <PhotoGallery />
    </Fragment>
  );
}

export default App;
