import React from "react";
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';

const config = {
    apiKey: 'AIzaSyCublmOZZ47OTX3ktg1f1JExx8QDapNaQI',
    authDomain: 'pumpkin-patch-c17f4.firebaseapp.com',
    // ...
  };

  firebase.initializeApp(config);const user = firebase.auth().currentUser
const HeaderLoginButton = props => {
    if (user != null){
    return (
        <button className={classes.button}  onClick={props.onLogOutClick} >
            
           
        <span>Logout</span>
    
    </button>
    );
}
if( user === null)
    return(
        <button className={classes.button}  onClick={props.onLoginClick } >
            
           
            <span>Login</span>
        
        </button>
    );
};

export default HeaderLoginButton;
