import { Fragment, useState} from "react";

import Header from "./components/Layout/Header";
import PhotoGallery from "./components/FrontPage/PhotoGallery"
import FrontBanner from "./components/FrontPage/FrontBanner"
import React from "react";
import ForgotPassword from "./components/Login/ForgotPassword";
import LoginMain from "./components/Login/LoginMain";



function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginActive, setLoginActive] = useState(false);
  const LoginActiveHandler = (event) => {
    setLoginActive(true);
  };
  const logOutHandler = (email, password) => {
    setIsLoggedIn(false);
    setLoginActive(false)
  };




  return (
    <Fragment>
      {/* {forgotPassword && <ForgotPassword onForgotPassword={ForgotPasswordEvent} />} */}
      <LoginMain setLoginActive = {setLoginActive}loginActive = {loginActive} />
      <Header isLoggedIn={isLoggedIn}  onLoginClick={LoginActiveHandler} onLogoutClick={logOutHandler}/>
      <FrontBanner />
      <PhotoGallery />
    </Fragment>
  );
}

export default App;
