import Card from '../UI/Card';
import classes from './AvailableMeals.css'
import MealItem from './MealItem/MealItem';
import img1 from '../../assets/bonai1.jfif';
import img2 from '../../assets/bonai2.jfif'

const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ];
  

const AvailableMeals = () => {
    const activeImage = document.querySelector(".product-image .active");
  const productImages = document.querySelectorAll(".image-list img");
  const navItem = document.querySelector('a.toggle-nav');
  
  function changeImage(event) {
    activeImage.src = event.target.value;
  }
  
  function toggleNavigation(){
    this.nextElementSibling.classList.toggle('active');
  }
  
  

    return (
        <section className="meals">
            <Card>
                <h1 className='summary' text-align = "center"  position = "relative">Gallery</h1>
                
<main>
  
    <div class="grid product">
      <div class="column-xs-12 column-md-7">
        <div class="product-gallery">
          <div class="product-image">
            <img class="active" src={img1}/>
          </div>
          <ul class="image-list">
            <li class="image-item"><img src={img1} value={img2} onClick={changeImage}/></li>
            <li class="image-item"><img src={img2} onClick={changeImage}/></li>
            <li class="image-item"><img src="https://source.unsplash.com/5WbYFH0kf_8"onClick={changeImage}/></li>
          </ul>
        </div>
      </div>
      </div>
      
</main>

                
            </Card>
        </section>
    )

};

export default AvailableMeals;
