import { Fragment, useState} from "react";
import Login from "./components/Login/Login";
import Header from "./components/Layout/Header";
import PhotoGallery from "./components/FrontPage/PhotoGallery"
import FrontBanner from "./components/FrontPage/FrontBanner"
import React from "react";
import ForgotPassword from "./components/Layout/ForgotPassword";



function App() {

  const DUMMY_USER=
  {
    email:"christcrom988@gmail.com",
    password:"phenoix1",
    sec1:"Django",
    sec2:"Burnet"
  };

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginActive, setLoginActive] = useState(false);
  const [forgotPassword, setForgotPassword] = useState(false);


  const loginHandler = (email, password) => {
    if(DUMMY_USER.email.includes(email) && DUMMY_USER.password.includes(password)){
      setIsLoggedIn(true);
    setLoginActive(false)
    }else{
      alert("You are not a member! Get out of here!");
    }
  };

  const logOutHandler = (email, password) => {
    setIsLoggedIn(false);
    setLoginActive(false)
  };

  const LoginActiveHandler = (event) => {
    setLoginActive(true);
  };

  const LoginNotActiveHandler = event =>{
    setLoginActive(false)
  };

  const ForgotPasswordEvent = event => {
    setForgotPassword(event);
  };


  return (
    <Fragment>
      {forgotPassword && <ForgotPassword onForgotPassword={ForgotPasswordEvent} />}
     {loginActive  && <Login onLogin={loginHandler} onLoginClick={LoginNotActiveHandler} onForgotPassword={ForgotPasswordEvent}/>}
      <Header isLoggedIn={isLoggedIn} onLoginClick={LoginActiveHandler} onLogoutClick={logOutHandler}/>
      <FrontBanner />
      <PhotoGallery />
    </Fragment>
  );
}

export default App;
